import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
    createUserWithEmailAndPassword,
    sendEmailVerification,
    signOut,
    signInWithEmailAndPassword,
} from 'firebase/auth';
import { auth } from '../firebaseConfig';
import { addData } from './firestore';
import { useAuth } from '../contexts/AuthContext';

const Registration = () => {
    const navigate = useNavigate();
    const { setUser, setUserData } = useAuth();
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        age: '',
        location: '',
        maritalStatus: '',
        dependents: '',
        monthlyIncome: '',
        password: '',
        confirmPassword: ''
    });
    const [errors, setErrors] = useState({});
    const [isAnimating, setIsAnimating] = useState(false);
    const [isEmailVerified, setIsEmailVerified] = useState(false);
    const [isCreatingAccount, setIsCreatingAccount] = useState(false);
    const [verificationSent, setVerificationSent] = useState(false);
    const [tempUserCredentials, setTempUserCredentials] = useState(null);
    const [isCheckingVerification, setIsCheckingVerification] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        if (errors[name]) setErrors(prev => ({ ...prev, [name]: '' }));
    };

    const validateStep = (step) => {
        const newErrors = {};
        let isValid = true;

        if (step === 1) {
            if (!formData.firstName.trim()) { newErrors.firstName = 'First Name is required'; isValid = false; }
            if (!formData.lastName.trim()) { newErrors.lastName = 'Last Name is required'; isValid = false; }
            if (!formData.email.trim()) { newErrors.email = 'Email is required'; isValid = false; }
            else if (!/\S+@\S+\.\S/.test(formData.email)) { newErrors.email = 'Email is invalid'; isValid = false; }
            if (!formData.password.trim()) { newErrors.password = 'Password is required'; isValid = false; }
            else if (formData.password.length < 6) { newErrors.password = 'Password must be at least 6 characters'; isValid = false; }
            if (formData.password !== formData.confirmPassword) { newErrors.confirmPassword = 'Passwords do not match'; isValid = false; }
            if (!formData.age.trim()) { newErrors.age = 'Age is required'; isValid = false; }
            else if (isNaN(formData.age) || parseInt(formData.age) < 18) { newErrors.age = 'Age must be a number and at least 18'; isValid = false; }
            if (!formData.location.trim()) { newErrors.location = 'Location is required'; isValid = false; }
            if (!formData.maritalStatus.trim()) { newErrors.maritalStatus = 'Marital Status is required'; isValid = false; }
            if (!formData.dependents.trim()) { newErrors.dependents = 'Number of Dependents is required'; isValid = false; }
            else if (isNaN(formData.dependents) || parseInt(formData.dependents) < 0) { newErrors.dependents = 'Dependents must be a non-negative number'; isValid = false; }
        } else if (step === 2) {
            if (!formData.monthlyIncome.trim()) { newErrors.monthlyIncome = 'Monthly Income is required'; isValid = false; }
            else if (isNaN(formData.monthlyIncome) || parseFloat(formData.monthlyIncome) <= 0) { newErrors.monthlyIncome = 'Monthly Income must be a positive number'; isValid = false; }
        }

        setErrors(newErrors);
        return isValid;
    };

    const handleNextStep = async () => {
        if (validateStep(currentStep)) {
            if (currentStep === 1) {
                if (!isEmailVerified) {
                    alert('Please verify your email before proceeding to the next step.');
                    return;
                }
                setIsAnimating(true);
                setTimeout(() => { setCurrentStep(2); setIsAnimating(false); }, 300);
            }
        }
    };

    const handlePreviousStep = () => {
        setIsAnimating(true);
        setTimeout(() => { setCurrentStep(1); setIsAnimating(false); }, 300);
    };

    const handleEmailVerification = async () => {
        if (!formData.email || !formData.password) {
            setErrors(prev => ({ ...prev, email: "Email and password are required." }));
            return;
        }
        setIsCreatingAccount(true);
        setErrors({});
        try {
            const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
            const user = userCredential.user;

            await sendEmailVerification(user);
            setVerificationSent(true);
            setTempUserCredentials({ email: formData.email, password: formData.password });

            await signOut(auth);
            setUser(null);
            setUserData(null);

            alert(`Verification email sent to ${formData.email}. Please check your email and click the verification link to continue.`);
        } catch (error) {
            setIsCreatingAccount(false);
            let errorMessage = 'Failed to create account. Please try again.';
            if (error.code === 'auth/email-already-in-use') errorMessage = 'This email is already in use.';
            else if (error.code === 'auth/invalid-email') errorMessage = 'The email address is invalid.';
            else if (error.code === 'auth/weak-password') errorMessage = 'The password is too weak.';
            setErrors(prev => ({ ...prev, email: errorMessage }));
        }
    };

    const handleCheckVerification = async () => {
        setIsCheckingVerification(true);
        try {
            if (tempUserCredentials) {
                const userCredential = await signInWithEmailAndPassword(auth, tempUserCredentials.email, tempUserCredentials.password);
                const user = userCredential.user;
                if (user.emailVerified) {
                    setIsEmailVerified(true);
                    alert('âœ… Email verified successfully! You can now continue.');
                    await signOut(auth);
                    setUser(null);
                    setUserData(null);
                } else {
                    alert('Email not verified yet. Please check your email.');
                    await signOut(auth);
                    setUser(null);
                    setUserData(null);
                }
            } else {
                alert('Verification credentials not available. Please resend the verification email.');
            }
        } catch (error) {
            console.error(error);
            alert('Error checking verification. Try again.');
        } finally {
            setIsCheckingVerification(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!isEmailVerified) { alert("Please verify your email before completing registration!"); return; }
        if (validateStep(currentStep)) {
            try {
                const user = auth.currentUser;
                if (!user) { alert("Please complete email verification first!"); return; }
                const userData = {
                    userId: user.uid,
                    email: user.email,
                    emailVerified: user.emailVerified,
                    firstName: formData.firstName,
                    lastName: formData.lastName,
                    age: parseInt(formData.age),
                    location: formData.location,
                    maritalStatus: formData.maritalStatus,
                    dependents: parseInt(formData.dependents),
                    monthlyIncome: parseFloat(formData.monthlyIncome),
                    password: formData.password,
                    createdAt: new Date(),
                    updatedAt: new Date()
                };
                const result = await addData('users', userData);
                if (result.success) {
                    await signOut(auth);
                    setUser(null);
                    setUserData(null);
                    alert("Registration successful! Please login to continue.");
                    navigate("/login");
                } else {
                    throw new Error(result.error);
                }
            } catch (error) {
                console.error(error);
                alert("An error occurred during registration. Please try again.");
            }
        }
    };

    return (
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8 min-h-screen flex flex-col justify-center">
            <div className="card w-full p-6 shadow-lg border rounded-lg">
                <div className="text-center mb-6">
                    <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                        <i className="fas fa-user-plus text-primary-600 text-2xl"></i>
                    </div>
                    <h1 className="text-3xl font-bold text-gray-900 mb-2">Create Your Account</h1>
                    <p className="text-gray-600">Join us to plan your perfect retirement</p>
                </div>

                <form onSubmit={handleSubmit} className={`transition-all duration-300 ${isAnimating ? 'opacity-0 transform translate-x-4' : 'opacity-100 transform translate-x-0'}`}>
                    {currentStep === 1 && (
                        <div className="space-y-6">
                            {/* Personal Info Form Fields Here */}
                            {/* ... same as your original code for step 1 ... */}
                            <div className="mt-4">
                                <button type="button" onClick={handleEmailVerification} disabled={isCreatingAccount || isEmailVerified || !formData.email || !formData.password} className={`w-full px-4 py-2 rounded-lg text-sm font-medium transition-colors ${isEmailVerified ? 'bg-green-100 text-green-700 border border-green-300' : isCreatingAccount ? 'bg-gray-100 text-gray-500 border border-gray-300 cursor-not-allowed' : 'bg-primary-600 text-white hover:bg-primary-700'}`}>
                                    {isCreatingAccount ? 'Sending...' : isEmailVerified ? 'Verified âœ“' : 'Send Verification Email'}
                                </button>
                            </div>

                            {verificationSent && !isEmailVerified && (
                                <div className="mt-3 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                                    <div className="flex items-center justify-between">
                                        <div className="flex-1">
                                            <div className="flex items-center mb-2">
                                                <i className="fas fa-envelope text-blue-600 mr-2"></i>
                                                <p className="text-sm text-blue-800 font-medium">Verification email sent!</p>
                                            </div>
                                            <p className="text-xs text-blue-600 mb-2">Please check your email and click the verification link to continue.</p>
                                            <p className="text-xs text-blue-500">After clicking the link, come back here and click "Check Verification" below.</p>
                                        </div>
                                        <button type="button" onClick={handleCheckVerification} disabled={isCheckingVerification} className={`px-4 py-2 text-sm font-medium rounded-lg transition-colors flex items-center ${isCheckingVerification ? 'bg-gray-400 text-gray-200 cursor-not-allowed' : 'bg-blue-600 text-white hover:bg-blue-700 shadow-md'}`}>
                                            {isCheckingVerification ? <> <i className="fas fa-spinner fa-spin mr-2"></i> Checking...</> : <> <i className="fas fa-check-circle mr-2"></i> Check Verification </>}
                                        </button>
                                    </div>
                                </div>
                            )}

                            {isEmailVerified && (
                                <div className="mt-3 p-3 bg-green-50 border border-green-200 rounded-lg">
                                    <div className="flex items-center">
                                        <i className="fas fa-check-circle text-green-600 mr-2"></i>
                                        <p className="text-sm text-green-800 font-medium">Email verified successfully! You can now proceed to the next step.</p>
                                    </div>
                                </div>
                            )}

                            <button type="button" onClick={handleNextStep} disabled={!isEmailVerified} className={`w-full py-3 px-4 rounded-lg text-lg font-semibold transition-colors duration-200 flex items-center justify-center ${!isEmailVerified ? 'bg-gray-300 text-gray-500 cursor-not-allowed' : 'bg-primary-600 hover:bg-primary-700 text-white'}`}>
                                Next Step
                            </button>
                        </div>
                    )}

                    {currentStep === 2 && (
                        <div className="space-y-6">
                            {/* Financial Info Fields */}
                            {/* ... same as your original code for step 2 ... */}
                            <div className="mt-4">
                                <button type="submit" className="w-full py-3 px-4 rounded-lg bg-primary-600 hover:bg-primary-700 text-white font-semibold">
                                    Complete Registration
                                </button>
                            </div>
                            <div className="mt-2">
                                <button type="button" onClick={handlePreviousStep} className="w-full py-2 px-4 rounded-lg bg-gray-200 hover:bg-gray-300 text-gray-800 font-medium">
                                    Back
                                </button>
                            </div>
                        </div>
                    )}
                </form>
            </div>
        </div>
    );
};

export default Registration;
