import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebaseConfig';
import { useAuth } from '../contexts/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [generalError, setGeneralError] = useState('');
  const navigate = useNavigate();
  const { setUser, setUserData } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear errors when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
    if (generalError) {
      setGeneralError('');
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.password.trim()) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Clear previous errors
    setErrors({});
    setGeneralError('');
    
    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      console.log('🔐 Attempting Firebase authentication...');
      console.log('📧 Email:', formData.email);
      
      // Firebase authentication with proper error handling
      const userCredential = await signInWithEmailAndPassword(
        auth, 
        formData.email.trim(), 
        formData.password
      );
      
      const user = userCredential.user;
      console.log('✅ Login successful:', user.email);

      // Set user data in context
      setUser(user);

      // Fetch user data from Firestore (non-blocking)
      const fetchUserData = async () => {
        try {
          const { checkUserExists } = await import('./firestore');
          const userCheck = await checkUserExists(user.email);
          if (userCheck.success && userCheck.exists) {
            setUserData(userCheck.user);
            console.log('✅ User data loaded from Firestore:', userCheck.user);
          }
        } catch (error) {
          console.log('⚠️ Could not fetch user data from Firestore:', error);
          // Continue with login even if Firestore fails
        }
      };

      // Fetch user data in background
      fetchUserData();

      // Success message and navigation
      console.log('🎉 Login successful! Redirecting to dashboard...');
      navigate('/dashboard');

    } catch (error) {
      console.error('❌ Login failed:', error);
      setIsLoading(false);
      
      let errorMessage = '';
      let errorField = 'general';

      // Handle specific Firebase Auth errors
      switch (error.code) {
        case 'auth/user-not-found':
          errorMessage = 'No account found with this email address. Please check your email or register for a new account.';
          errorField = 'email';
          break;
        case 'auth/wrong-password':
          errorMessage = 'Incorrect password. Please try again.';
          errorField = 'password';
          break;
        case 'auth/invalid-email':
          errorMessage = 'Please enter a valid email address.';
          errorField = 'email';
          break;
        case 'auth/invalid-credential':
          errorMessage = 'Invalid email or password. Please check your credentials and try again.';
          errorField = 'general';
          break;
        case 'auth/too-many-requests':
          errorMessage = 'Too many failed login attempts. Please try again later.';
          errorField = 'general';
          break;
        case 'auth/user-disabled':
          errorMessage = 'This account has been disabled. Please contact support.';
          errorField = 'general';
          break;
        case 'auth/network-request-failed':
          errorMessage = 'Network error. Please check your internet connection and try again.';
          errorField = 'general';
          break;
        case 'auth/operation-not-allowed':
          errorMessage = 'Email/password authentication is not enabled. Please contact support.';
          errorField = 'general';
          break;
        default:
          errorMessage = `Login failed: ${error.message}`;
          errorField = 'general';
      }

      // Set error in appropriate field
      if (errorField === 'general') {
        setGeneralError(errorMessage);
      } else {
        setErrors(prev => ({
          ...prev,
          [errorField]: errorMessage
        }));
      }
    }
  };

  return (
    <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 min-h-screen flex flex-col justify-center">
      <div className="card w-full p-6 shadow-lg border rounded-lg">
        <div className="text-center mb-6">
          <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-sign-in-alt text-primary-600 text-2xl"></i>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome Back</h1>
          <p className="text-gray-600">Sign in to your AI Retirement Planner account</p>
        </div>

        {/* General Error Message */}
        {generalError && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <div className="flex items-center">
              <i className="fas fa-exclamation-triangle text-red-600 mr-2"></i>
              <p className="text-red-800 text-sm font-medium">{generalError}</p>
            </div>
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="form-label">Email Address *</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={`form-input ${errors.email ? 'border-red-500' : ''}`}
              placeholder="Enter your email address"
              disabled={isLoading}
            />
            {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
          </div>

          <div>
            <label className="form-label">Password *</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={`form-input ${errors.password ? 'border-red-500' : ''}`}
              placeholder="Enter your password"
              disabled={isLoading}
            />
            {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
          </div>

          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <input
                id="remember-me"
                name="remember-me"
                type="checkbox"
                className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                disabled={isLoading}
              />
              <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-900">
                Remember me
              </label>
            </div>

            <div className="text-sm">
              <a href="#" className="font-medium text-primary-600 hover:text-primary-500">
                Forgot your password?
              </a>
            </div>
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className={`btn-primary w-full py-3 px-4 rounded-md text-lg font-semibold transition-colors duration-200 ${
              isLoading ? 'opacity-50 cursor-not-allowed' : ''
            }`}
          >
            {isLoading ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Signing In...
              </>
            ) : (
              <>
                <i className="fas fa-sign-in-alt mr-2"></i>
                Sign In
              </>
            )}
          </button>
        </form>

        <div className="mt-6 text-center">
          <p className="text-sm text-gray-600">
            Don't have an account?{' '}
            <Link to="/registration" className="font-medium text-primary-600 hover:text-primary-500">
              Create Account
            </Link>
          </p>
        </div>

        {/* Features */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="card p-4 text-center border rounded-lg">
            <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-shield-alt text-primary-600"></i>
            </div>
            <h3 className="font-semibold text-gray-900 mb-1">Secure Login</h3>
            <p className="text-sm text-gray-600">Bank-level encryption</p>
          </div>

          <div className="card p-4 text-center border rounded-lg">
            <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-mobile-alt text-primary-600"></i>
            </div>
            <h3 className="font-semibold text-gray-900 mb-1">Mobile Access</h3>
            <p className="text-sm text-gray-600">Access anywhere</p>
          </div>

          <div className="card p-4 text-center border rounded-lg">
            <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-clock text-primary-600"></i>
            </div>
            <h3 className="font-semibold text-gray-900 mb-1">24/7 Support</h3>
            <p className="text-sm text-gray-600">Always available</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
