import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebaseConfig';
import { checkUserExists, testFirestoreConnection, verifyUserData } from './firestore';
// import { testFirebaseConnection } from '../test-firebase';
import { useAuth } from '../contexts/AuthContext';
import { db } from '../firebaseConfig';
import { collection, getDocs, addDoc } from 'firebase/firestore';

// Firebase connection test function
const testFirebaseConnection = async () => {
  try {
    console.log('ðŸ” Testing Firebase connection...');
    console.log('ðŸ” Database object:', db);
    
    // Test 1: Try to read from users collection
    console.log('ðŸ” Test 1: Reading users collection...');
    const usersCollection = collection(db, 'users');
    console.log('ðŸ” Users collection reference:', usersCollection);
    
    const querySnapshot = await getDocs(usersCollection);
    console.log('ðŸ” Query snapshot size:', querySnapshot.size);
    console.log('ðŸ” Query snapshot empty:', querySnapshot.empty);
    
    const users = [];
    querySnapshot.forEach((doc) => {
      const userData = { id: doc.id, ...doc.data() };
      users.push(userData);
      console.log('ðŸ” User document:', userData);
    });
    
    console.log('ðŸ” All users:', users);
    
    // Test 2: Try to write a test document
    console.log('ðŸ” Test 2: Writing test document...');
    const testData = {
      test: true,
      timestamp: new Date().toISOString(),
      message: 'Firebase connection test'
    };
    
    const docRef = await addDoc(collection(db, 'test'), testData);
    console.log('ðŸ” Test document created with ID:', docRef.id);
    
    return {
      success: true,
      usersCount: querySnapshot.size,
      users: users,
      testDocId: docRef.id
    };
  } catch (error) {
    console.error('âŒ Firebase connection test failed:', error);
    console.error('âŒ Error details:', {
      code: error.code,
      message: error.message,
      stack: error.stack
    });
    return {
      success: false,
      error: error.message,
      code: error.code
    };
  }
};

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { setUser, setUserData } = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password.trim()) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      setIsLoading(true);

      try {
        // First, check if user exists in our database
        console.log('Checking if user exists in database for email:', formData.email);
        console.log('Firebase config:', process.env.REACT_APP_FIREBASE_PROJECT_ID);

        const userCheck = await checkUserExists(formData.email);
        console.log('Database check result:', userCheck);

        // Debug: Let's also check what's in localStorage
        const localData = localStorage.getItem('userData');
        console.log('ðŸ” LocalStorage data:', localData ? JSON.parse(localData) : 'No data');

        // Also check if there are any other user-related items in localStorage
        console.log('ðŸ” All localStorage keys:', Object.keys(localStorage));
        console.log('ðŸ” All localStorage values:', Object.keys(localStorage).map(key => ({ key, value: localStorage.getItem(key) })));

        // If database check failed, try localStorage fallback
        if (!userCheck.success) {
          console.error('Database check failed:', userCheck.error);
          console.log('Falling back to localStorage check...');

          // Fallback: Check localStorage for user data
          const localUserData = localStorage.getItem('userData');
          if (localUserData) {
            const userData = JSON.parse(localUserData);
            if (userData.email === formData.email) {
              console.log('User found in localStorage, proceeding with Firebase authentication...');
              // Continue with Firebase authentication even if database check failed
            } else {
              setIsLoading(false);
              setErrors(prev => ({
                ...prev,
                email: 'User not found in our system. Please register first.'
              }));
              return;
            }
          } else {
            setIsLoading(false);
            setErrors(prev => ({
              ...prev,
              email: 'User not found in our system. Please register first.'
            }));
            return;
          }
        } else if (!userCheck.exists) {
          // Database check succeeded but user not found
          console.log('User not found in database');
          setIsLoading(false);
          setErrors(prev => ({
            ...prev,
            email: 'User not found in our system. Please register first.'
          }));
          return;
        } else {
          console.log('User found in database, proceeding with Firebase authentication...');
        }

        // If user exists in database, proceed with Firebase authentication
        try {
          console.log('Attempting Firebase authentication...');
          const userCredential = await signInWithEmailAndPassword(auth, formData.email, formData.password);
          const user = userCredential.user;

          console.log('Login successful:', user);

          // Set user data in context
          setUser(user);
          if (userCheck.success && userCheck.exists) {
            setUserData(userCheck.user);
            console.log('User data set from database:', userCheck.user);
          } else {
            // If database check failed but localStorage has data, use that
            const localUserData = localStorage.getItem('userData');
            if (localUserData) {
              const userData = JSON.parse(localUserData);
              setUserData(userData);
              console.log('User data set from localStorage:', userData);
            }
          }

          alert('Login successful! Welcome to AI Retirement Planner');
          navigate('/dashboard');
        } catch (firebaseAuthError) {
          console.error('Firebase authentication failed:', firebaseAuthError);

          // If Firebase Auth fails but user exists in database, 
          // this means the user was registered but Firebase Auth creation failed
          if (firebaseAuthError.code === 'auth/invalid-credential' ||
            firebaseAuthError.code === 'auth/user-not-found') {

            console.log('User exists in database but not in Firebase Auth. Attempting to recreate...');

            try {
              // Try to recreate the Firebase Auth user
              const userCredential = await createUserWithEmailAndPassword(auth, formData.email, formData.password);
              console.log('Firebase Auth user recreated successfully:', userCredential.user);

              alert('Account recreated successfully! Welcome to AI Retirement Planner');
              navigate('/dashboard');
              return;
            } catch (recreateError) {
              console.error('Failed to recreate Firebase Auth user:', recreateError);

              // If recreation fails, allow login with database verification only
              if (recreateError.code === 'auth/email-already-in-use') {
                console.log('Email already in use in Firebase Auth. Allowing database-only login...');
                alert('Welcome back! Logging you in with database verification.');
                navigate('/dashboard');
                return;
              }

              let errorMessage = 'Authentication failed. Please try registering again or contact support.';

              // Provide specific error messages
              if (recreateError.code === 'auth/weak-password') {
                errorMessage = 'Password is too weak. Please use a stronger password.';
              } else if (recreateError.code === 'auth/invalid-email') {
                errorMessage = 'Invalid email address. Please check your email format.';
              }

              setIsLoading(false);
              setErrors(prev => ({
                ...prev,
                email: errorMessage
              }));
              return;
            }
          }

          // Re-throw other Firebase errors
          throw firebaseAuthError;
        }
      } catch (error) {
        setIsLoading(false);
        let errorMessage = 'Login failed. Please try again.';

        switch (error.code) {
          case 'auth/user-not-found':
            errorMessage = 'No Firebase account found with this email.';
            break;
          case 'auth/wrong-password':
            errorMessage = 'Incorrect password.';
            break;
          case 'auth/invalid-email':
            errorMessage = 'Please enter a valid email address.';
            break;
          case 'auth/too-many-requests':
            errorMessage = 'Too many failed attempts. Please try again later.';
            break;
          default:
            errorMessage = error.message;
        }

        setErrors(prev => ({
          ...prev,
          email: errorMessage
        }));
      }
    }
  };

  return (
    <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8 min-h-screen flex flex-col justify-center">
      <div className="card w-full p-6 shadow-lg border rounded-lg">
        <div className="text-center mb-6">
          <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-sign-in-alt text-primary-600 text-2xl"></i>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome Back</h1>
          <p className="text-gray-600">Sign in to your AI Retirement Planner account</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="form-label">Email Address</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              className={`form-input ${errors.email ? 'border-red-500' : ''}`}
              placeholder="Enter your email address"
            />
            {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
          </div>

          <div>
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              className={`form-input ${errors.password ? 'border-red-500' : ''}`}
              placeholder="Enter your password"
            />
            {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className="btn btn-primary w-full"
          >
            {isLoading ? (
              <>
                <i className="fas fa-spinner fa-spin mr-2"></i>
                Signing In...
              </>
            ) : (
              <>
                <i className="fas fa-sign-in-alt mr-2"></i>
                Sign In
              </>
            )}
          </button>
        </form>

        {/* Debug buttons - remove these in production */}
        <div className="mt-4 space-y-2">
          <button
            type="button"
            onClick={async () => {
              console.log('ðŸ” Testing database connection...');
              try {
                // Test Firestore connection
                console.log('ðŸ” Testing Firestore connection...');
                const firestoreTest = await testFirestoreConnection();
                console.log('ðŸ” Firestore test result:', firestoreTest);

                // Test user check with the actual email from form
                console.log('ðŸ” Testing user check...');
                const userCheck = await checkUserExists(formData.email || 'test@example.com');
                console.log('ðŸ” User check result:', userCheck);

                // Verify user data
                console.log('ðŸ” Testing user verify...');
                const userVerify = await verifyUserData(formData.email || 'test@example.com');
                console.log('ðŸ” User verify result:', userVerify);

                const resultMessage = `Firestore: ${firestoreTest.success ? 'Connected' : 'Failed'}\nUser Check: ${userCheck.success ? 'Working' : 'Failed'}\nUser Verify: ${userVerify.success ? 'Found' : 'Not Found'}`;
                console.log('ðŸ” Final result:', resultMessage);
                alert(resultMessage);
              } catch (error) {
                console.error('âŒ Database test error:', error);
                console.error('âŒ Error details:', {
                  code: error.code,
                  message: error.message,
                  stack: error.stack
                });
                alert('Database test failed: ' + error.message);
              }
            }}
            className="w-full py-2 px-4 bg-gray-500 text-white rounded-md text-sm"
          >
            Test Database Connection
          </button>

          <button
            type="button"
            onClick={async () => {
              console.log('ðŸ” Checking all users in database...');
              try {
                const { db } = await import('../firebaseConfig');
                const { collection, getDocs } = await import('firebase/firestore');

                console.log('ðŸ” Database object:', db);
                console.log('ðŸ” Collection reference:', collection(db, 'users'));

                const querySnapshot = await getDocs(collection(db, 'users'));
                console.log('ðŸ” Total users in database:', querySnapshot.size);
                console.log('ðŸ” Query snapshot empty:', querySnapshot.empty);

                const users = [];
                querySnapshot.forEach((doc) => {
                  const userData = { id: doc.id, ...doc.data() };
                  users.push(userData);
                  console.log('ðŸ” User document ID:', doc.id);
                  console.log('ðŸ” User data:', userData);
                });

                console.log('ðŸ” All users array:', users);
                alert(`Found ${users.length} users in database. Check console for details.`);
              } catch (error) {
                console.error('âŒ Error checking users:', error);
                console.error('âŒ Error details:', {
                  code: error.code,
                  message: error.message,
                  stack: error.stack
                });
                alert('Error: ' + error.message);
              }
            }}
            className="w-full py-2 px-4 bg-blue-500 text-white rounded-md text-sm"
          >
            Check All Users in Database
          </button>

          <button
            type="button"
            onClick={async () => {
              console.log('ðŸ” Testing Firebase connection directly...');
              try {
                const result = await testFirebaseConnection();
                console.log('ðŸ” Firebase test result:', result);

                if (result.success) {
                  alert(`Firebase: Connected\nUsers: ${result.usersCount}\nTest Doc: ${result.testDocId}`);
                } else {
                  alert(`Firebase: Failed\nError: ${result.error}\nCode: ${result.code}`);
                }
              } catch (error) {
                console.error('âŒ Firebase test error:', error);
                alert('Firebase test failed: ' + error.message);
              }
            }}
            className="w-full py-2 px-4 bg-green-500 text-white rounded-md text-sm"
          >
            Test Firebase Connection
          </button>
        </div>

        {/* Features */}
        <div className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4">
          <div className="card p-4 text-center border rounded-lg">
            <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-shield-alt text-primary-600"></i>
            </div>
            <h3 className="font-semibold text-gray-900 mb-1">Secure Login</h3>
            <p className="text-sm text-gray-600">Bank-level encryption</p>
          </div>

          <div className="card p-4 text-center border rounded-lg">
            <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-mobile-alt text-primary-600"></i>
            </div>
            <h3 className="font-semibold text-gray-900 mb-1">Mobile Access</h3>
            <p className="text-sm text-gray-600">Access anywhere</p>
          </div>

          <div className="card p-4 text-center border rounded-lg">
            <div className="w-12 h-12 bg-primary-100 rounded-lg flex items-center justify-center mx-auto mb-3">
              <i className="fas fa-clock text-primary-600"></i>
            </div>
            <h3 className="font-semibold text-gray-900 mb-1">24/7 Support</h3>
            <p className="text-sm text-gray-600">Always available</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
