import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Registration = () => {
    const [formData, setFormData] = useState({
        name: '',
        age: '',
        email: '',
        location: '',
        maritalStatus: '',
        dependents: '',
        basicIncome: '',
        password: '',
        confirmPassword: ''
    });

    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
        // Clear error when user starts typing
        if (errors[name]) {
            setErrors(prev => ({
                ...prev,
                [name]: ''
            }));
        }
    };

    const validateForm = () => {
        const newErrors = {};

        if (!formData.name.trim()) newErrors.name = 'Name is required';
        if (!formData.age || formData.age < 18 || formData.age > 100) {
            newErrors.age = 'Please enter a valid age (18-100)';
        }
        if (!formData.email.trim()) {
            newErrors.email = 'Email is required';
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = 'Please enter a valid email address';
        }
        if (!formData.location.trim()) newErrors.location = 'Location is required';
        if (!formData.maritalStatus) newErrors.maritalStatus = 'Please select marital status';
        if (!formData.dependents || formData.dependents < 0) {
            newErrors.dependents = 'Please enter a valid number of dependents';
        }
        if (!formData.basicIncome || formData.basicIncome < 0) {
            newErrors.basicIncome = 'Please enter a valid income amount';
        }
        if (!formData.password) {
            newErrors.password = 'Password is required';
        } else if (formData.password.length < 8) {
            newErrors.password = 'Password must be at least 8 characters long';
        }
        if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (validateForm()) {
            // Handle form submission
            console.log('Form submitted:', formData);
            alert('Registration successful! Please check your email for verification.');
        }
    };

    const handleReset = () => {
        setFormData({
            name: '',
            age: '',
            email: '',
            location: '',
            maritalStatus: '',
            dependents: '',
            basicIncome: '',
            password: '',
            confirmPassword: ''
        });
        setErrors({});
    };

    return (
        <div className="min-h-screen bg-gray-50 py-12">
            <div className="max-w-2xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="card">
                    <div className="card-header">
                        <div className="text-center">
                            <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center mx-auto mb-4">
                                <i className="fas fa-user-plus text-primary-600 text-2xl"></i>
                            </div>
                            <h1 className="text-3xl font-bold text-gray-900 mb-2">Create Your Account</h1>
                            <p className="text-gray-600">Join our AI-powered retirement planning platform</p>
                        </div>
                    </div>

                    <div className="card-body">
                        <form onSubmit={handleSubmit} className="space-y-6">
                            {/* Personal Information */}
                            <div className="space-y-4">
                                <h3 className="text-lg font-semibold text-gray-900 border-b border-gray-200 pb-2">
                                    Personal Information
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div>
                                        <label htmlFor="name" className="form-label">
                                            Full Name *
                                        </label>
                                        <input
                                            type="text"
                                            id="name"
                                            name="name"
                                            value={formData.name}
                                            onChange={handleChange}
                                            className={`form-input ${errors.name ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                            placeholder="Enter your full name"
                                        />
                                        {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name}</p>}
                                    </div>

                                    <div>
                                        <label htmlFor="age" className="form-label">
                                            Age *
                                        </label>
                                        <input
                                            type="number"
                                            id="age"
                                            name="age"
                                            value={formData.age}
                                            onChange={handleChange}
                                            className={`form-input ${errors.age ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                            placeholder="Enter your age"
                                            min="18"
                                            max="100"
                                        />
                                        {errors.age && <p className="text-red-500 text-sm mt-1">{errors.age}</p>}
                                    </div>
                                </div>

                                <div>
                                    <label htmlFor="email" className="form-label">
                                        Email Address *
                                    </label>
                                    <input
                                        type="email"
                                        id="email"
                                        name="email"
                                        value={formData.email}
                                        onChange={handleChange}
                                        className={`form-input ${errors.email ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                        placeholder="Enter your email (verification required)"
                                    />
                                    {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
                                </div>

                                <div>
                                    <label htmlFor="location" className="form-label">
                                        Location *
                                    </label>
                                    <input
                                        type="text"
                                        id="location"
                                        name="location"
                                        value={formData.location}
                                        onChange={handleChange}
                                        className={`form-input ${errors.location ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                        placeholder="Enter your city, state"
                                    />
                                    {errors.location && <p className="text-red-500 text-sm mt-1">{errors.location}</p>}
                                </div>
                            </div>

                            {/* Family Information */}
                            <div className="space-y-4">
                                <h3 className="text-lg font-semibold text-gray-900 border-b border-gray-200 pb-2">
                                    Family Information
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div>
                                        <label htmlFor="maritalStatus" className="form-label">
                                            Marital Status *
                                        </label>
                                        <select
                                            id="maritalStatus"
                                            name="maritalStatus"
                                            value={formData.maritalStatus}
                                            onChange={handleChange}
                                            className={`form-input ${errors.maritalStatus ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                        >
                                            <option value="">Select marital status</option>
                                            <option value="single">Single</option>
                                            <option value="married">Married</option>
                                            <option value="divorced">Divorced</option>
                                            <option value="widowed">Widowed</option>
                                        </select>
                                        {errors.maritalStatus && <p className="text-red-500 text-sm mt-1">{errors.maritalStatus}</p>}
                                    </div>

                                    <div>
                                        <label htmlFor="dependents" className="form-label">
                                            Number of Dependents *
                                        </label>
                                        <input
                                            type="number"
                                            id="dependents"
                                            name="dependents"
                                            value={formData.dependents}
                                            onChange={handleChange}
                                            className={`form-input ${errors.dependents ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                            placeholder="Enter number of dependents"
                                            min="0"
                                        />
                                        {errors.dependents && <p className="text-red-500 text-sm mt-1">{errors.dependents}</p>}
                                    </div>
                                </div>
                            </div>

                            {/* Financial Information */}
                            <div className="space-y-4">
                                <h3 className="text-lg font-semibold text-gray-900 border-b border-gray-200 pb-2">
                                    Basic Income Information
                                </h3>

                                <div>
                                    <label htmlFor="basicIncome" className="form-label">
                                        Annual Income (₹) *
                                    </label>
                                    <input
                                        type="number"
                                        id="basicIncome"
                                        name="basicIncome"
                                        value={formData.basicIncome}
                                        onChange={handleChange}
                                        className={`form-input ${errors.basicIncome ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                        placeholder="Enter your annual income"
                                        min="0"
                                    />
                                    {errors.basicIncome && <p className="text-red-500 text-sm mt-1">{errors.basicIncome}</p>}
                                </div>
                            </div>

                            {/* Security */}
                            <div className="space-y-4">
                                <h3 className="text-lg font-semibold text-gray-900 border-b border-gray-200 pb-2">
                                    Security
                                </h3>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    <div>
                                        <label htmlFor="password" className="form-label">
                                            Password *
                                        </label>
                                        <input
                                            type="password"
                                            id="password"
                                            name="password"
                                            value={formData.password}
                                            onChange={handleChange}
                                            className={`form-input ${errors.password ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                            placeholder="Create a strong password"
                                        />
                                        {errors.password && <p className="text-red-500 text-sm mt-1">{errors.password}</p>}
                                    </div>

                                    <div>
                                        <label htmlFor="confirmPassword" className="form-label">
                                            Confirm Password *
                                        </label>
                                        <input
                                            type="password"
                                            id="confirmPassword"
                                            name="confirmPassword"
                                            value={formData.confirmPassword}
                                            onChange={handleChange}
                                            className={`form-input ${errors.confirmPassword ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}`}
                                            placeholder="Confirm your password"
                                        />
                                        {errors.confirmPassword && <p className="text-red-500 text-sm mt-1">{errors.confirmPassword}</p>}
                                    </div>
                                </div>
                            </div>

                            {/* Form Actions */}
                            <div className="flex flex-col sm:flex-row gap-4 pt-6">
                                <button
                                    type="submit"
                                    className="btn btn-primary flex-1"
                                >
                                    <i className="fas fa-user-plus mr-2"></i>
                                    Create Account
                                </button>
                                <button
                                    type="button"
                                    onClick={handleReset}
                                    className="btn btn-outline flex-1"
                                >
                                    <i className="fas fa-undo mr-2"></i>
                                    Reset Form
                                </button>
                            </div>

                            {/* Login Link */}
                            <div className="text-center pt-4">
                                <p className="text-gray-600">
                                    Already have an account?{' '}
                                    <Link to="/login" className="text-primary-600 hover:text-primary-700 font-medium">
                                        Sign in here
                                    </Link>
                                </p>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Registration;
