import React, { useState, useEffect } from "react";
import { sendEmailVerification, createUserWithEmailAndPassword } from "firebase/auth";
import { collection, addDoc } from "firebase/firestore";
import { auth, db } from "../firebaseConfig";
import { useNavigate } from "react-router-dom";

const Registration = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [isEmailVerified, setIsEmailVerified] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    age: "",
    email: "",
    password: "",
    location: "",
    maritalStatus: "",
    dependents: "",
    monthlyIncome: "",
    fixedExpenses: "",
    variableExpenses: "",
    currentSavings: "",
    retirementAge: "",
  });

  const [errors, setErrors] = useState({});

  // handle form changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    if (errors[name]) setErrors((prev) => ({ ...prev, [name]: "" }));
  };

  // validate form fields
  const validateStep = (step) => {
    const newErrors = {};
    if (step === 1) {
      if (!formData.name.trim()) newErrors.name = "Name is required";
      if (!formData.email.trim()) newErrors.email = "Email is required";
      else if (!/\S+@\S+\.\S+/.test(formData.email))
        newErrors.email = "Invalid email format";
      if (!formData.password.trim())
        newErrors.password = "Password is required";
    } else if (step === 2) {
      if (!formData.monthlyIncome.trim())
        newErrors.monthlyIncome = "Monthly income is required";
      if (!formData.fixedExpenses.trim())
        newErrors.fixedExpenses = "Fixed expenses are required";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Step 1 — Send verification email
  const handleSendVerification = async (e) => {
    e.preventDefault();
    if (!validateStep(1)) return;
    setIsLoading(true);
    try {
      const userCredential = await createUserWithEmailAndPassword(
        auth,
        formData.email,
        formData.password
      );
      await sendEmailVerification(userCredential.user);
      alert("Verification email sent! Please check your inbox.");
    } catch (error) {
      console.error("Error sending verification email:", error);
      alert(error.message);
    } finally {
      setIsLoading(false);
    }
  };

  // Step 1 — Check verification status
  const handleCheckVerification = async () => {
    if (auth.currentUser) {
      await auth.currentUser.reload();
      if (auth.currentUser.emailVerified) {
        setIsEmailVerified(true);
        alert("✅ Email verified successfully! You can now continue.");
      } else {
        alert("❌ Email not verified yet. Please verify your email first.");
      }
    } else {
      alert("No user found. Please register again.");
    }
  };

  // Step 2 — Save user data to Firestore
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateStep(2)) return;
    setIsLoading(true);
    try {
      await addDoc(collection(db, "users"), formData);
      alert("Registration successful!");
      navigate("/login");
    } catch (error) {
      console.error("Error saving user:", error);
      alert("Something went wrong while saving your data.");
    } finally {
      setIsLoading(false);
    }
  };

  // ✅ Prevent skipping verification step
  useEffect(() => {
    if (currentStep === 2 && !isEmailVerified) {
      setCurrentStep(1);
    }
  }, [currentStep, isEmailVerified]);

  // ✅ Proceed to next step only if verified
  const handleNextStep = () => {
    if (!isEmailVerified) {
      alert("Please verify your email before proceeding.");
      return;
    }
    if (validateStep(currentStep)) {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentStep(2);
        setIsAnimating(false);
      }, 300);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded-xl shadow-md mt-10">
      <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">
        AI Retirement Planner Registration
      </h2>

      {/* STEP 1 */}
      {currentStep === 1 && (
        <form onSubmit={handleSendVerification} className="space-y-5">
          <div>
            <label className="block text-gray-700 font-medium">Name</label>
            <input
              type="text"
              name="name"
              className={`w-full border px-3 py-2 rounded ${
                errors.name ? "border-red-500" : "border-gray-300"
              }`}
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your full name"
            />
            {errors.name && (
              <p className="text-red-500 text-sm">{errors.name}</p>
            )}
          </div>

          <div>
            <label className="block text-gray-700 font-medium">Email</label>
            <input
              type="email"
              name="email"
              className={`w-full border px-3 py-2 rounded ${
                errors.email ? "border-red-500" : "border-gray-300"
              }`}
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
            />
            {errors.email && (
              <p className="text-red-500 text-sm">{errors.email}</p>
            )}
          </div>

          <div>
            <label className="block text-gray-700 font-medium">Password</label>
            <input
              type="password"
              name="password"
              className={`w-full border px-3 py-2 rounded ${
                errors.password ? "border-red-500" : "border-gray-300"
              }`}
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
            />
            {errors.password && (
              <p className="text-red-500 text-sm">{errors.password}</p>
            )}
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className="w-full bg-blue-600 text-white font-medium py-2 rounded hover:bg-blue-700 transition"
          >
            {isLoading ? "Sending..." : "Send Verification Email"}
          </button>

          <button
            type="button"
            onClick={handleCheckVerification}
            className="w-full bg-green-600 text-white font-medium py-2 rounded hover:bg-green-700 transition"
          >
            Check Verification
          </button>

          <button
            type="button"
            onClick={handleNextStep}
            disabled={!isEmailVerified}
            className={`w-full font-medium py-2 rounded transition ${
              isEmailVerified
                ? "bg-indigo-600 hover:bg-indigo-700 text-white"
                : "bg-gray-300 cursor-not-allowed text-gray-600"
            }`}
          >
            Next Step
          </button>
        </form>
      )}

      {/* STEP 2 */}
      {currentStep === 2 && isEmailVerified && (
        <form onSubmit={handleSubmit} className="space-y-5 mt-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-4">
            Financial Details
          </h3>

          <div>
            <label className="block text-gray-700 font-medium">
              Monthly Income
            </label>
            <input
              type="number"
              name="monthlyIncome"
              className={`w-full border px-3 py-2 rounded ${
                errors.monthlyIncome ? "border-red-500" : "border-gray-300"
              }`}
              value={formData.monthlyIncome}
              onChange={handleChange}
              placeholder="Enter your monthly income"
            />
            {errors.monthlyIncome && (
              <p className="text-red-500 text-sm">{errors.monthlyIncome}</p>
            )}
          </div>

          <div>
            <label className="block text-gray-700 font-medium">
              Fixed Expenses
            </label>
            <input
              type="number"
              name="fixedExpenses"
              className={`w-full border px-3 py-2 rounded ${
                errors.fixedExpenses ? "border-red-500" : "border-gray-300"
              }`}
              value={formData.fixedExpenses}
              onChange={handleChange}
              placeholder="Enter your fixed expenses"
            />
            {errors.fixedExpenses && (
              <p className="text-red-500 text-sm">{errors.fixedExpenses}</p>
            )}
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className="w-full bg-blue-600 text-white font-medium py-2 rounded hover:bg-blue-700 transition"
          >
            {isLoading ? "Saving..." : "Complete Registration"}
          </button>
        </form>
      )}
    </div>
  );
};

export default Registration;
